import React from 'react'
import Employes from './Employes';
import { useState } from 'react';
import { Button, Form, FormControl, Modal } from 'react-bootstrap';
import AddFormat from './AddForms';

 const EmployeeLists = ({filterbysearch , itemsData}) => {

  
// const [employes , setEmployes  ] = useState([

// {id:'1',name:'ali',email:'ali@gm.com',address:'baghdad',phone:'555-555',img:'images/a1.jpg'},
// {id:'2',name:'Mujtaba',email:'Mujtaba@gm.com',address:'baghdad',phone:'458-852',img:'images/IMG_20170718_123614_860.jpg'},
// {id:'3',name:'Ahmed',email:'Ahmed@gm.com',address:'baghdad',phone:'487-032',img:'images/jin.jpg'},
// {id:'4',name:'Saif',email:'Saif@gm.com',address:'baghdad',phone:'105-659',img:'images/photo_2021-01-06_14-03-05.jpg'},
// {id:'5',name:'Salman',email:'Salman@gm.com',address:'baghdad',phone:'594-759',img:'images/a2.jpg'},
// ])
const [show , setShow] = useState(false)

const handelShow = ()=> setShow(true);
const handelClose = ()=> setShow(false);

const [searchValue, setSearchValue] = useState('')
  const onSearch = () =>{
   // filterbysearch(searchValue)
  //  setSearchValue('') //من اسوي بحث راح تختفي الكتابة داخل البتن
  }

  return (
    <>
<div className="table-title">
	<div className="row">
		<div className="col-sm-3">
						<h2>Manage <b>Employees</b></h2>
					</div>
					<div className="col-sm-4">
						<Button onClick={handelShow} className="btn btn-success" data-toggle="modal"><i className="material-icons">&#xE148;</i> <span>Add New Employee</span></Button>					
		      </div>
          <div className="col-sm-5">
          <Form className="d-flex" >
            <FormControl
              type="text"
              placeholder="search"
              className="mx-2"
              aria-label="Search"
              onChange={(e)=> setSearchValue(e.target.value)}
              value={searchValue}
            />
            <Button onClick={()=> onSearch} variant="success">Search</Button>
          </Form>
          </div>
		</div>
</div>
<table className="table table-striped table-hover">
				<thead>
					<tr>
            <th>images</th>
						<th>Name</th>
						<th>Email</th>
						<th>Address</th>
						<th>Phone</th>
						<th>Actions</th>
					</tr>
				</thead>
				<tbody>
                    {
                        employes.map(item =>
                            <tr key={item.id}>
                                <Employes item={item} />
                                 
                            </tr>
                            
                            )
                    }
                
 </tbody>
</table>
{/* اظهار قائمة اضافة موظف جديد */}
<Modal show={show} onHide={handelClose} >
<Modal.Header closeButton>
          <Modal.Title>Add Employee</Modal.Title>
  </Modal.Header>
         
<Modal.Body>
          <AddFormat/>
</Modal.Body>

<Modal.Footer>
  <Button onClick={handelClose} variant="danger">
      Close Button
  </Button>
</Modal.Footer>
          
</Modal>
  </>
                   
  )
}
export default EmployeeLists;
