import { Button,Form } from 'react-bootstrap';
import { useState } from 'react';
import React from 'react'



 const EditeEmployes = ({ addEmploye}) => {
    const [newEmployee , setnewEmployee] = useState({});
    const onInputChange = (e) =>{
        setnewEmployee({...newEmployee ,[e.target.name]: e.target.value})
    }
     const {name , email, address,phone} = newEmployee ;
     const handlSubmit = (e) => {
         e.preventDefault();
         addEmploye(newEmployee)
       
     }
  return (
    
<Form >
    <Form.Group>
        <Form.Control
        style={{margin:'10px'}}
            type="text"
            placeholder="Name *"
            name='name'
            required
             />
    </Form.Group>

    <Form.Group>
        <Form.Control
        style={{margin:'10px'}}
            type="text"
            placeholder="Email *"
            name='email'
            required
             />
    </Form.Group>

    <Form.Group>
        <Form.Control
        style={{margin:'10px'}}
            as="textarea"
            placeholder="Address"
            name='address'
            rows={3} />
    </Form.Group>

    <Form.Group>
        <Form.Control
        style={{margin:'10px'}}
            type="text"
            placeholder="phone"
            name='phone'
             />
    </Form.Group>

    <Button variant="outline-success" type="submit" block>
        Edite Employee
    </Button>
 </Form>
 
  )
}
export default EditeEmployes;
