import { Button,Form } from 'react-bootstrap';
import { useState } from 'react';
import React from 'react'



 const EditeEmployes = ({ theEmployee , updateEmployee}) => {
    // const [newEmployee , setnewEmployee] = useState({});
    // const onInputChange = (e) =>{
    //     setnewEmployee({...newEmployee ,[e.target.name]: e.target.value})
    // }
    //  const {name , email, address,phone} = newEmployee ;
    //  const handlSubmit = (e) => {
    //      e.preventDefault();
    //      EditeEmployes(newEmployee)
       
    //  }
    const id = theEmployee.id ;
    const [name , setName] = useState(theEmployee.name);
    const [email , setEmail] = useState(theEmployee.email);
    const [address , setAddress] = useState(theEmployee.address);
    const [phone , setPhone] = useState(theEmployee.phone);
  return (
    
<Form >
    <Form.Group>
        <Form.Control
        style={{margin:'10px'}}
            type="text"
            placeholder="Name *"
            name='name'
            value={name}
            
            required
             />
    </Form.Group>

    <Form.Group>
        <Form.Control
        style={{margin:'10px'}}
            type="text"
            placeholder="Email *"
            name='email'
            value={email}
            required
             />
    </Form.Group>

    <Form.Group>
        <Form.Control
        style={{margin:'10px'}}
            as="textarea"
            placeholder="Address"
            name='address'
            value={address}
            rows={3} />
    </Form.Group>

    <Form.Group>
        <Form.Control
        style={{margin:'10px'}}
            type="text"
            placeholder="phone"
            name='phone'
            value={phone}
             />
    </Form.Group>

    <Button variant="outline-success" type="submit" block>
        Edite Employee
    </Button>
 </Form>
 
  )
}
export default EditeEmployes;
