import React from 'react'
import Employes from './Employes';
import { useState } from 'react';
import { Alert, Button, Form, FormControl, Modal } from 'react-bootstrap';
import AddFormat from './AddForms';

 const EmployeeLists = ({filterbysearch , employes , handelDelet  , addEmploye, updateEmployee})=>{
 
const [show , setShow] = useState(false)

const handelShow = ()=> setShow(true);
const handelClose = ()=> setShow(false);

const [searchValue, setSearchValue] = useState()
  const onSearch = () =>{
   filterbysearch(searchValue)
  // setSearchValue('') //من اسوي بحث راح تختفي الكتابة داخل البتن
  }

  const [showAlert , setShowalert] = useState(true)

  return (
    <>
<div className="table-title">
	<div className="row">
		<div className="col-sm-3">
						<h2>Manage <b>Employees</b></h2>
					</div>
					<div className="col-sm-4">
						<Button onClick={handelShow} className="btn btn-success" data-toggle="modal"><i className="material-icons">&#xE148;</i> <span>Add New Employeeo</span></Button>					
		      </div>
          <div className="col-sm-5">
          <Form className="d-flex" >
            <FormControl
              type="text"
              placeholder="Search"
              className="mx-2"
              aria-label="Search"
              onChange={(e)=> setSearchValue(e.target.value)}
              value={searchValue}
            />
            <Button onClick={onSearch} variant="success" style={{marginLeft:"-85px"}}>Search</Button>
          </Form>
          </div>
		</div>
</div>
<Alert variant="danger" onClose={() => setShow(false)} dismissible></Alert>
<table className="table table-striped table-hover">
				<thead>
					<tr>
            <th>images</th>
						<th>Name</th>
						<th>Email</th>
						<th>Address</th>
						<th>Phone</th>
						<th>Actions</th>
					</tr>
				</thead>
				<tbody>
                    {
                        employes.sort((a,b)=>(a.name < b.name ? -1 : 1)).map((item , index) => //تعديل حسب الاحرف sort
                            <tr key={index}>
                                <Employes handelDelet={handelDelet}
                                 index={index}
                                 item={item}
                                 updateEmployee={updateEmployee} />
                                 
                            </tr>
                            
                            ) 
                    }
                
 </tbody>
</table>
{/* اظهار قائمة اضافة موظف جديد */}
<Modal show={show} onHide={handelClose} >
<Modal.Header closeButton>
          <Modal.Title>Add Employee</Modal.Title>
  </Modal.Header>
         
<Modal.Body>
          <AddFormat  addEmploye={addEmploye}/>
</Modal.Body>

<Modal.Footer>
  <Button onClick={handelClose} variant="danger">
      Close Button
  </Button>
</Modal.Footer>
          
</Modal>
  </>
                   
  )
}
export default EmployeeLists;
