import { Button,Form } from 'react-bootstrap';
import { useState } from 'react';
import React from 'react'



 const AddFormat = ({data , addEmploye}) => {
  //  const addEmploye = useState(data);
    // const [newEmployee , setnewEmployee] = useState({
    //     name:"" , email:"", address:"" , phone:""
    // });
    const [newEmployee , setnewEmployee] = useState({});
    const [name1 , setName1] = useState('')

    const onInputChange = (e) =>{
        setnewEmployee({[e.target.name]: e.target.value})
        setName1({[e.target.name]: e.targe.valut})
    }
    const {name , email, address,phone} = newEmployee ;
    const handlSubmit = (e) => {
        e.preventDefault();
        addEmploye(name1 )
       // console.log(name)
    }
  return (
    
<Form onSubmit={handlSubmit}>
    <Form.Group>
        <Form.Control
        style={{margin:'10px'}}
            type="text"
            placeholder="Name *"
            name='name'
            value={name}
            onChange = {(e) => onInputChange(e) }
            required />
    </Form.Group>

    <Form.Group>
        <Form.Control
        style={{margin:'10px'}}
            type="email"
            placeholder="Email *"
            name='email'
            value={email}
            onChange = {(e) => onInputChange(e) }
            required />
    </Form.Group>

    <Form.Group>
        <Form.Control
        style={{margin:'10px'}}
            as="textarea"
            placeholder="Address"
            name='address'
            value={address}
            onChange = {(e) => onInputChange(e) }
            rows={3} />
    </Form.Group>

    <Form.Group>
        <Form.Control
        style={{margin:'10px'}}
            type="text"
            placeholder="phone"
            name='phone'
            value={phone}
            onChange = {(e) => onInputChange(e) }
             />
    </Form.Group>

    <Button variant="outline-success" type="submit" block>
        Add New Employee
    </Button>
 </Form>
 
  )
}
export default AddFormat;
