import { Button,Form } from 'react-bootstrap';
import { useState } from 'react';
import React from 'react'



 const AddFormat = ({data}) => {
    const addEmploye = useState(data);
    const [newEmployee , setnewEmployee] = useState({
        name:"" , email:"", address:"" , phone
    });

    const handlSubmit = (e) => {
        e.preventDefault();
        console.log(newEmployee);
    }
  return (
    
<Form onSubmit={handlSubmit}>
    <Form.Group>
        <Form.Control
        style={{margin:'10px'}}
            type="text"
            placeholder="Name *"
            value={newEmployee}
            onChange = {(e) => setnewEmployee(e.target.value) }
            required />
    </Form.Group>

    <Form.Group>
        <Form.Control
        style={{margin:'10px'}}
            type="email"
            placeholder="Email *"
            required />
    </Form.Group>

    <Form.Group>
        <Form.Control
        style={{margin:'10px'}}
            as="textarea"
            placeholder="Address"
            rows={3} />
    </Form.Group>

    <Form.Group>
        <Form.Control
        style={{margin:'10px'}}
            type="text"
            placeholder="phone"
             />
    </Form.Group>

    <Button variant="outline-success" type="submit" block>
        Add New Employee
    </Button>
 </Form>
 
  )
}
export default AddFormat;
